function loadAssignments(){var outlet=document.getElementById("templateOutlet");$(outlet).empty();var content=document.getElementById("assignmentTemplate").content;DataService.loadAssignments(AuthService.getUser()._key).success(function(data){var numAssignments=data.length;$("[data-numassignments]").html(numAssignments);for(var i=0;i<data.length;i++){var obj=data[i];content.querySelector("[data-status]").textContent="active",content.querySelector("[data-papername]").textContent=obj.paper.title,content.querySelector("[data-papermeta]").textContent="PMC-ID: "+obj.paper.pmcID,content.querySelector("[data-progress]").style.width=obj.assignment.completion+"%",content.querySelector("[data-progresstext]").textContent=obj.assignment.completion+"%",content.querySelector("[data-studyname]").textContent=obj.project.name,content.querySelector("[data-studymeta]").textContent=obj.project.date_created,content.querySelector("[data-button]").dataset.assignmentKey=obj.assignment._key,outlet.appendChild(content.cloneNode(!0))}})}function load(buttonElement){localStorage.assignmentKey=buttonElement.dataset.assignmentKey,window.location="paper-coder.html"}$(document).ready(function(){Site.run(),loadAssignments()});