function loadProjects(){for(var projectTemplate=document.querySelector("#projectPanel"),ul=document.querySelector("#project-ul");ul.hasChildNodes();)ul.removeChild(ul.lastChild);DataService.getProjectsData().success(function(data){for(var pr=0;pr<data.projects.length;pr++){var projectData=data.projects[pr];projectTemplate.content.querySelector("[data-name]").textContent=projectData.name,projectTemplate.content.querySelector("[data-key]").dataset.key=projectData._key;var clone=projectTemplate.content.cloneNode(!0);ul.appendChild(clone)}})}function submitNewProjectForm(){var form=document.forms.createProjectForm,projectObject={name:form.name.value,description:form.description.value};DataService.postProject(projectObject).success(function(response){swal({title:"Project Created!",text:"Project Key: "+response.projectKey,type:"success",showCancelButton:!1,confirmButtonClass:"btn-success",confirmButtonText:"OK",closeOnConfirm:!1}),loadProjects()})}function openProject(buttonElement){buttonElement.dataSet.projectKey}$(document).ready(function(){AppProjects.run(),loadProjects()});var sampleProject={name:"Front End Stuff",description:"this project was created by the website"};