function loadProjects(){for(var projectTemplate=document.querySelector("#projectPanel"),ul=document.querySelector("#project-ul");ul.hasChildNodes();)ul.removeChild(ul.lastChild);DataService.getProjectsData().success(function(data){for(var pr=0;pr<data.projects.length;pr++){var projectData=data.projects[pr];projectTemplate.content.querySelector("[data-name]").textContent=projectData.name;var clone=projectTemplate.content.cloneNode(!0);ul.appendChild(clone)}})}function submitNewProjectForm(){var form=document.forms.createProjectForm,name=form.name.value,desc=form.description.value,projectObject={name:name,description:desc};DataService.postProject(projectObject).success(function(response){swal({title:"Project Created!",text:"Project Key: "+response.projectKey,type:"success",showCancelButton:!1,confirmButtonClass:"btn-success",confirmButtonText:"OK",closeOnConfirm:!1}),loadProjects()})}$(document).ready(function(){AppProjects.run(),loadProjects()});const sampleProject={name:"Front End Stuff",description:"this project was created by the website"};